,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'DECLARE',
'  l_file_name   VARCHAR2(255) := :P2_CSV;',
'  l_blob        BLOB;',
'  l_rows        PLS_INTEGER := 0;',
'BEGIN',
'  IF l_file_name IS NULL THEN',
'    apex_error.add_error(',
'      p_message => ''Please choose a CSV file first.'',',
'      p_display_location => apex_error.c_inline_in_notification );',
'    RETURN;',
'  END IF;',
'',
'  -- Read uploaded file from APEX temp files (no created_by column!)',
'  SELECT blob_content',
'    INTO l_blob',
'    FROM apex_application_temp_files',
'   WHERE name = l_file_name',
'   ORDER BY created_on DESC',
'   FETCH FIRST 1 ROWS ONLY;',
'',
'  -- Parse CSV; APEX will auto-detect format from file name/content',
'  FOR r IN (',
'    SELECT line_number,',
'           col001 AS farm_code,',
'           col002 AS contractor,',
'           col003 AS delivered_at_txt,',
'           col004 AS net_tonnes_txt,',
'           col005 AS ext_ticket_id',
'      FROM apex_data_parser.parse(',
'             p_content   => l_blob,',
'             p_file_name => l_file_name )',
'  ) LOOP',
'    -- Skip header row if present',
'    IF r.line_number = 1 AND upper(r.farm_code) IN (''FARM_CODE'',''FARM'',''FARMID'') THEN',
'      CONTINUE;',
'    END IF;',
'',
'    INSERT INTO deliv_stg (farm_code, contractor, delivered_at, net_tonnes, ext_ticket_id)',
'    VALUES (',
'      r.farm_code,',
'      r.contractor,',
'      TO_DATE(r.delivered_at_txt, ''YYYY-MM-DD HH24:MI''),',
'      TO_NUMBER(REPLACE(r.net_tonnes_txt, '','', '''')),',
'      r.ext_ticket_id',
'    );',
'',
'    l_rows := l_rows + 1;',
'  END LOOP;',
'',
'  COMMIT;',
'',
'  -- Rely on the process Success Message property; no need to set it here',
'EXCEPTION',
'  WHEN NO_DATA_FOUND THEN',
'    apex_error.add_error(',
'      p_message => ''Cannot read the uploaded file (temp file expired). Please upload again.'',',
'      p_display_location => apex_error.c_inline_in_notification );',
'  WHEN OTHERS THEN',
'    apex_error.add_error(',
'      p_message => ''Upload failed: ''||SQLERRM,',
'      p_display_location => apex_error.c_inline_in_notification );',
'    ROLLBACK;',
'END;',
''))
,p_process_clob_language=>'PLSQL'
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
,p_process_when_button_id=>wwv_flow_imp.id(10604585299288164)
,p_process_success_message=>'Uploaded rows into DELIV_STG.'
,p_internal_uid=>4403391061080419
);
