'    ROUND(DBMS_RANDOM.VALUE(14,20), 2)  AS brix,',
'    ROUND(DBMS_RANDOM.VALUE(8,16), 2)   AS fibre',
'  FROM consignment c',
'  WHERE c.delivered_at >= TRUNC(SYSDATE) - 14',
'    AND NOT EXISTS (SELECT 1 FROM lab_result lr',
'                    WHERE lr.consignmENT_id = c.consignmENT_id)',
'    AND NOT EXISTS (SELECT 1 FROM lab_stg s',
'                    WHERE s.ext_ticket_id = c.ext_ticket_id);',
'',
'  COMMIT;',
'END;',
''))
,p_process_clob_language=>'PLSQL'
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
,p_process_when_button_id=>wwv_flow_imp.id(10605091161288169)
,p_process_success_message=>'Generated staged lab results.'
