);
wwv_flow_imp_page.create_page_process(
 p_id=>wwv_flow_imp.id(10605466084288172)
,p_process_sequence=>20
,p_process_point=>'ON_SUBMIT_BEFORE_COMPUTATION'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'PRC_LAB_IMPORT'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'BEGIN',
'  /* Upsert exactly ONE lab row per consignment.',
'     If multiple staged rows map to the same consignment, keep the latest STG_ID. */',
'  MERGE INTO lab_result lr',
'  USING (',
'    SELECT CONSIGNMENT_ID, POL, BRIX, FIBRE',
'    FROM (',
'      SELECT',
'        c.CONSIGNMENT_ID,',
'        s.POL, s.BRIX, s.FIBRE,',
'        ROW_NUMBER() OVER (',
'          PARTITION BY c.CONSIGNMENT_ID',
'          ORDER BY s.STG_ID DESC',
'        ) rn',
'      FROM lab_stg s',
'      JOIN consignment c',
'        ON c.EXT_TICKET_ID = s.EXT_TICKET_ID',
'    )',
'    WHERE rn = 1',
'  ) src',
'  ON (lr.CONSIGNMENT_ID = src.CONSIGNMENT_ID)',
'  WHEN MATCHED THEN',
'    UPDATE SET lr.POL = src.POL,',
'               lr.BRIX = src.BRIX,',
'               lr.FIBRE = src.FIBRE,',
'               lr.CREATED_AT = SYSDATE',
'  WHEN NOT MATCHED THEN',
'    INSERT (CONSIGNMENT_ID, POL, BRIX, FIBRE, CREATED_AT)',
'    VALUES (src.CONSIGNMENT_ID, src.POL, src.BRIX, src.FIBRE, SYSDATE);',
'',
'  -- clear staging after successful import',
'  DELETE FROM lab_stg;',
'  COMMIT;',
'END;',
''))
,p_process_clob_language=>'PLSQL'
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
,p_process_when_button_id=>wwv_flow_imp.id(10605299781288171)
,p_process_success_message=>'Imported lab results into core and cleared staging.'
,p_internal_uid=>4404092216080426
);
