 p_id=>wwv_flow_imp.id(10607272474288190)
,p_process_sequence=>30
,p_process_point=>'ON_SUBMIT_BEFORE_COMPUTATION'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'PRC_LAB_UPLOAD'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'DECLARE',
'  l_file_name   VARCHAR2(255) := :P3_LAB_CSV; -- <-- adjust item name',
'  l_blob        BLOB;',
'  l_rows        PLS_INTEGER := 0;',
'BEGIN',
'  IF l_file_name IS NULL THEN',
'    apex_error.add_error(',
'      p_message => ''Please choose a CSV file first.'',',
'      p_display_location => apex_error.c_inline_in_notification );',
'    RETURN;',
'  END IF;',
'',
'  SELECT blob_content',
'    INTO l_blob',
'    FROM apex_application_temp_files',
'   WHERE name = l_file_name',
'   ORDER BY created_on DESC',
'   FETCH FIRST 1 ROWS ONLY;',
'',
'  -- CSV layout: EXT_TICKET_ID, POL, BRIX, FIBRE  (header row optional)',
'  FOR r IN (',
'    SELECT line_number,',
'           col001 AS ext_ticket_id,',
'           col002 AS pol_txt,',
'           col003 AS brix_txt,',
'           col004 AS fibre_txt',
'      FROM apex_data_parser.parse(',
'             p_content   => l_blob,',
'             p_file_name => l_file_name )',
'  ) LOOP',
'    IF r.line_number = 1 AND upper(r.ext_ticket_id) IN (''EXT_TICKET_ID'',''TICKET'',''TICKET_ID'') THEN',
'      CONTINUE;',
'    END IF;',
'',
'    INSERT INTO lab_stg (ext_ticket_id, pol, brix, fibre)',
'    VALUES (',
'      r.ext_ticket_id,',
'      TO_NUMBER(REPLACE(r.pol_txt,   '','', '''')),',
'      TO_NUMBER(REPLACE(r.brix_txt,  '','', '''')),',
'      TO_NUMBER(REPLACE(r.fibre_txt, '','', ''''))',
'    );',
'',
'    l_rows := l_rows + 1;',
'  END LOOP;',
'',
'  COMMIT;',
'EXCEPTION',
'  WHEN NO_DATA_FOUND THEN',
'    apex_error.add_error(',
'      p_message => ''Cannot read the uploaded file (temp file expired). Please upload again.'',',
'      p_display_location => apex_error.c_inline_in_notification );',
'  WHEN OTHERS THEN',
'    apex_error.add_error(',
'      p_message => ''Upload failed: ''||SQLERRM,',
'      p_display_location => apex_error.c_inline_in_notification );',
'    ROLLBACK;',
'END;',
''))
,p_process_clob_language=>'PLSQL'
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
,p_process_when_button_id=>wwv_flow_imp.id(10607154109288189)
,p_process_success_message=>'Uploaded rows into LAB_STG.'
,p_internal_uid=>4405898606080444
);
end;
